package com.syntax.class07;

public class WhileLoopExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num=1;

while (num<=10 ) {
	
	
	System.out.print(num+" ");num++;
}
System.out.println();
int a=10;
while (a <100) {
	System.out.print(a+" ");
	a++;
	System.out.println();
	int b=10;
	while (b >=10) {
		System.out.println(b+" ");
		b--;
	}
	System.out.println(" ");
	int c=100;
	
	while (c>=50) {
		System.out.print(c+" ");
		c--;
		
	}
	System.out.println(" ");
	
int d=2;
	
	while (d<=20) {
		System.out.print(d+" ");
		d+=2;
}
	
	System.out.println(" ");
	
int e=1;
	
	while (e<=20) {
		
		if (e % 2 ==0) {
		System.out.print(e+" e");
		
		
	}
		e+=2;
	}

}
	}
}
